import React, { useState, useEffect } from 'react';
import style from './style.module.css';

function Seat({ id, x, y, width, height, onClick, isSelected }) {
  return (
    <rect
      className={`place ${isSelected ? 'selected' : ''}`}
      x={x}
      y={y}
      width={width}
      height={height}
      rx="6"
      data-seat-id={id}
      onClick={onClick}
    />
  );
}

function HallSchema({ svgData }) {
  const [selectedSeats, setSelectedSeats] = useState([]);
  const [scale, setScale] = useState(1);

  const handleSeatClick = (id) => {
    setSelectedSeats((prevSelectedSeats) => {
      if (prevSelectedSeats.includes(id)) {
        return prevSelectedSeats.filter((seatId) => seatId !== id);
      } else {
        return [...prevSelectedSeats, id];
      }
    });
  };

  const handleIncrease = () => {
    setScale((prevScale) => (prevScale < 2.0 ? prevScale + 0.1 : prevScale));
  };

  const handleDecrease = () => {
    setScale((prevScale) => (prevScale > 0.1 ? prevScale - 0.1 : 0.1));
  };

  return (
    <div className={style.wrap}>
      <svg
        className={style.schema}
        style={{ transform: `scale(${scale})` }}
        dangerouslySetInnerHTML={{ __html: svgData }}
      >
        {/* Рендерим компоненты <Seat> вместо вставки SVG-кода */}
        {selectedSeats.map((id) => (
          <Seat
            key={id}
            id={id}
            x={x}
            y={y}
            width={width}
            height={height}
            onClick={() => handleSeatClick(id)}
            isSelected={true}
          />
        ))}
      </svg>
      <div className={style.buttons}>
        <button onClick={handleIncrease}>+</button>
        <button onClick={handleDecrease}>-</button>
      </div>
    </div>
  );
}

export default HallSchema;
